def parse_table(table: str) -> None:
    for num, line in enumerate(table.splitlines()):
        if num < 2:
            continue
        average_weight: str
        (_, species_id, species_name, genus_id, description, average_length, average_weight, primary_color, _) = (v.strip() for v in line.split('|'))
        print((int(species_id), species_name, int(genus_id), description, int(average_length), int(average_weight.replace(' ', '')), primary_color), ',')


